contracts/ExpenseFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "./GroupTreasury.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";
import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";

contract ExpenseFactory is Ownable, Pausable, ReentrancyGuard {
    struct GroupInfo {
        address groupAddress;
        string name;
        address creator;
        uint256 createdAt;
        bool active;
        uint256 memberCount;
        uint256 totalExpenses;
        uint256 lastActivity;
    }

    struct GroupStats {
        uint256 totalGroups;
        uint256 activeGroups;
        uint256 totalMembers;
        uint256 totalExpenses;
    }

    mapping(address => address[]) private userGroups;
    mapping(address => GroupInfo) public groupInfos;
    mapping(address => bool) public authorizedCreators; // For limiting group creation

    address[] private allGroups;
    uint256 public maxGroupsPerUser = 10;
    uint256 public creationFee = 0; // Wei amount for creating groups

    event GroupCreated(
        address indexed group,
        address indexed creator,
        string name,
        uint256 timestamp
    );
    event GroupDeactivated(address indexed group, uint256 timestamp);
    event CreationFeeUpdated(uint256 newFee);
    event MaxGroupsPerUserUpdated(uint256 newMax);
    event CreatorAuthorized(address indexed creator);
    event CreatorDeauthorized(address indexed creator);

    modifier onlyAuthorizedCreator() {
        require(
            authorizedCreators[msg.sender] || owner() == msg.sender,
            "Not authorized to create groups"
        );
        _;
    }

    modifier validGroupAddress(address _groupAddress) {
        require(_groupAddress != address(0), "Invalid group address");
        require(
            groupInfos[_groupAddress].active,
            "Group not found or inactive"
        );
        _;
    }

    constructor() Ownable(msg.sender) {
        // Initialize owner as authorized creator
        authorizedCreators[msg.sender] = true;
    }

    function createGroup(
        string memory _name,
        string memory _creatorNickname
    )
        external
        payable
        onlyAuthorizedCreator
        whenNotPaused
        nonReentrant
        returns (address)
    {
        require(
            bytes(_name).length > 0 && bytes(_name).length <= 50,
            "Invalid group name length"
        );
        require(
            bytes(_creatorNickname).length > 0 &&
                bytes(_creatorNickname).length <= 32,
            "Invalid nickname length"
        );
        require(msg.value >= creationFee, "Insufficient creation fee");
        require(
            userGroups[msg.sender].length < maxGroupsPerUser,
            "Maximum groups per user reached"
        );

        // Create new group with 3 parameters
        GroupTreasury newGroup = new GroupTreasury(
            _name,
            msg.sender,
            _creatorNickname
        );
        address groupAddress = address(newGroup);

        GroupInfo memory groupInfo = GroupInfo({
            groupAddress: groupAddress,
            name: _name,
            creator: msg.sender,
            createdAt: block.timestamp,
            active: true,
            memberCount: 1, // Creator is first member
            totalExpenses: 0,
            lastActivity: block.timestamp
        });

        groupInfos[groupAddress] = groupInfo;
        userGroups[msg.sender].push(groupAddress);
        allGroups.push(groupAddress);

        // Refund excess payment
        if (msg.value > creationFee) {
            (bool success, ) = msg.sender.call{value: msg.value - creationFee}(
                ""
            );
            require(success, "Refund failed");
        }

        emit GroupCreated(groupAddress, msg.sender, _name, block.timestamp);
        return groupAddress;
    }

    function addUserToGroup(
        address _user,
        address _groupAddress
    ) external validGroupAddress(_groupAddress) {
        // Verify the caller is the group contract itself
        require(
            msg.sender == _groupAddress,
            "Only group contract can add users"
        );

        // Add group to user's list if not already present
        address[] storage currentGroups = userGroups[_user];
        bool alreadyMember = false;

        for (uint256 i = 0; i < currentGroups.length; i++) {
            if (currentGroups[i] == _groupAddress) {
                alreadyMember = true;
                break;
            }
        }

        if (!alreadyMember) {
            require(
                currentGroups.length < maxGroupsPerUser,
                "User has reached maximum groups"
            );
            userGroups[_user].push(_groupAddress);
            groupInfos[_groupAddress].memberCount++;
            groupInfos[_groupAddress].lastActivity = block.timestamp;
        }
    }

    function updateGroupActivity(
        address _groupAddress
    ) external validGroupAddress(_groupAddress) {
        require(
            msg.sender == _groupAddress,
            "Only group contract can update activity"
        );
        groupInfos[_groupAddress].lastActivity = block.timestamp;
    }

    function deactivateGroup(
        address _groupAddress
    ) external validGroupAddress(_groupAddress) {
        require(
            msg.sender == groupInfos[_groupAddress].creator ||
                msg.sender == _groupAddress ||
                msg.sender == owner(),
            "Not authorized to deactivate group"
        );

        groupInfos[_groupAddress].active = false;
        emit GroupDeactivated(_groupAddress, block.timestamp);
    }

    // View functions with pagination
    function getUserGroups(
        address _user
    ) external view returns (address[] memory) {
        return _getActiveUserGroups(_user);
    }

    function getUserGroupsWithInfo(
        address _user
    ) external view returns (GroupInfo[] memory) {
        address[] memory userGroupAddresses = _getActiveUserGroups(_user);
        GroupInfo[] memory activeGroupInfos = new GroupInfo[](
            userGroupAddresses.length
        );

        for (uint256 i = 0; i < userGroupAddresses.length; i++) {
            activeGroupInfos[i] = groupInfos[userGroupAddresses[i]];
        }

        return activeGroupInfos;
    }

    function _getActiveUserGroups(
        address _user
    ) internal view returns (address[] memory) {
        address[] memory userGroupAddresses = userGroups[_user];
        uint256 activeCount = 0;

        // Count active groups for user
        for (uint256 i = 0; i < userGroupAddresses.length; i++) {
            if (groupInfos[userGroupAddresses[i]].active) {
                activeCount++;
            }
        }

        // Create array of active groups
        address[] memory activeGroups = new address[](activeCount);
        uint256 index = 0;

        for (uint256 i = 0; i < userGroupAddresses.length; i++) {
            if (groupInfos[userGroupAddresses[i]].active) {
                activeGroups[index] = userGroupAddresses[i];
                index++;
            }
        }

        return activeGroups;
    }

    function getAllGroupsPaginated(
        uint256 _offset,
        uint256 _limit
    ) external view returns (address[] memory groups, uint256 total) {
        // Count active groups first
        uint256 activeCount = 0;
        for (uint256 i = 0; i < allGroups.length; i++) {
            if (groupInfos[allGroups[i]].active) {
                activeCount++;
            }
        }

        if (_offset >= activeCount) {
            return (new address[](0), activeCount);
        }

        uint256 end = _offset + _limit;
        if (end > activeCount) {
            end = activeCount;
        }

        uint256 length = end - _offset;
        address[] memory paginatedGroups = new address[](length);
        uint256 activeIndex = 0;
        uint256 resultIndex = 0;

        for (uint256 i = 0; i < allGroups.length && resultIndex < length; i++) {
            if (groupInfos[allGroups[i]].active) {
                if (activeIndex >= _offset) {
                    paginatedGroups[resultIndex] = allGroups[i];
                    resultIndex++;
                }
                activeIndex++;
            }
        }

        return (paginatedGroups, activeCount);
    }

    function getGroupInfo(
        address _groupAddress
    )
        external
        view
        validGroupAddress(_groupAddress)
        returns (GroupInfo memory)
    {
        return groupInfos[_groupAddress];
    }

    function getFactoryStats() external view returns (GroupStats memory) {
        uint256 activeCount = 0;
        uint256 totalMembers = 0;
        uint256 totalExpenses = 0;

        for (uint256 i = 0; i < allGroups.length; i++) {
            GroupInfo storage group = groupInfos[allGroups[i]];
            if (group.active) {
                activeCount++;
                totalMembers += group.memberCount;
                totalExpenses += group.totalExpenses;
            }
        }

        return
            GroupStats({
                totalGroups: allGroups.length,
                activeGroups: activeCount,
                totalMembers: totalMembers,
                totalExpenses: totalExpenses
            });
    }

    function searchGroups(
        string memory _nameQuery
    ) external view returns (address[] memory) {
        bytes memory query = bytes(_nameQuery);
        require(query.length > 0, "Empty search query");

        address[] memory results = new address[](allGroups.length);
        uint256 resultCount = 0;

        for (uint256 i = 0; i < allGroups.length; i++) {
            if (groupInfos[allGroups[i]].active) {
                bytes memory groupName = bytes(groupInfos[allGroups[i]].name);
                if (_contains(groupName, query)) {
                    results[resultCount] = allGroups[i];
                    resultCount++;
                }
            }
        }

        // Resize array to actual results
        assembly {
            mstore(results, resultCount)
        }

        return results;
    }

    function _contains(
        bytes memory _text,
        bytes memory _query
    ) internal pure returns (bool) {
        if (_query.length > _text.length) return false;

        for (uint256 i = 0; i <= _text.length - _query.length; i++) {
            bool found = true;
            for (uint256 j = 0; j < _query.length; j++) {
                if (_text[i + j] != _query[j]) {
                    found = false;
                    break;
                }
            }
            if (found) return true;
        }
        return false;
    }

    // Admin functions
    function setCreationFee(uint256 _newFee) external onlyOwner {
        creationFee = _newFee;
        emit CreationFeeUpdated(_newFee);
    }

    function setMaxGroupsPerUser(uint256 _newMax) external onlyOwner {
        require(_newMax > 0 && _newMax <= 100, "Invalid max groups value");
        maxGroupsPerUser = _newMax;
        emit MaxGroupsPerUserUpdated(_newMax);
    }

    function authorizeCreator(address _creator) external onlyOwner {
        require(_creator != address(0), "Invalid creator address");
        authorizedCreators[_creator] = true;
        emit CreatorAuthorized(_creator);
    }

    function deauthorizeCreator(address _creator) external onlyOwner {
        require(_creator != owner(), "Cannot deauthorize owner");
        authorizedCreators[_creator] = false;
        emit CreatorDeauthorized(_creator);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function withdrawFees() external onlyOwner nonReentrant {
        uint256 balance = address(this).balance;
        require(balance > 0, "No fees to withdraw");

        (bool success, ) = owner().call{value: balance}("");
        require(success, "Withdrawal failed");
    }

    // Getter functions
    function getTotalGroupsCount() external view returns (uint256) {
        return allGroups.length;
    }

    function getActiveGroupsCount() external view returns (uint256) {
        uint256 activeCount = 0;
        for (uint256 i = 0; i < allGroups.length; i++) {
            if (groupInfos[allGroups[i]].active) {
                activeCount++;
            }
        }
        return activeCount;
    }

    function isAuthorizedCreator(
        address _creator
    ) external view returns (bool) {
        return authorizedCreators[_creator] || _creator == owner();
    }

    // Emergency functions
    function emergencyDeactivateGroup(
        address _groupAddress
    ) external onlyOwner {
        require(
            groupInfos[_groupAddress].groupAddress != address(0),
            "Group does not exist"
        );
        groupInfos[_groupAddress].active = false;
        emit GroupDeactivated(_groupAddress, block.timestamp);
    }

    receive() external payable {
        // Allow contract to receive ETH for creation fees and other payments
        // This function is called when ETH is sent directly to the contract
        // without any function call data

        // Emit an event to track direct ETH deposits
        emit ETHReceived(msg.sender, msg.value, block.timestamp);
    }

    // Event for tracking direct ETH deposits
    event ETHReceived(
        address indexed sender,
        uint256 amount,
        uint256 timestamp
    );
}

contracts/GroupTreasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/utils/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

interface IExpenseFactory {
    function addUserToGroup(address _user, address _groupAddress) external;
}

contract GroupTreasury is ReentrancyGuard, Ownable, Pausable {
    struct Expense {
        uint256 id;
        string description;
        uint256 totalAmount;
        address paidBy;
        address[] participants;
        uint256[] shares;
        bool settled;
        uint256 timestamp;
        bytes32 receiptHash; // For off-chain receipt storage
    }

    struct Member {
        address wallet;
        string nickname;
        uint256 totalOwed; // How much this member owes others
        uint256 totalOwing; // How much others owe this member
        bool active;
        uint256 joinedAt;
    }

    struct Settlement {
        address debtor;
        address creditor;
        uint256 amount;
        uint256 timestamp;
        bytes32 txHash;
    }

    string public groupName;
    uint256 private nextExpenseId;
    IExpenseFactory public immutable factory;

    mapping(address => Member) public members;
    mapping(uint256 => Expense) public expenses;
    mapping(address => mapping(address => uint256)) public balances; // debtor => creditor => amount
    mapping(uint256 => Settlement) public settlements;

    address[] public memberList;
    uint256[] public expenseList;
    uint256[] public settledExpenses;

    uint256 public constant MAX_MEMBERS = 50;
    uint256 public constant MAX_PARTICIPANTS_PER_EXPENSE = 20;
    uint256 private nextSettlementId;

    event MemberAdded(
        address indexed member,
        string nickname,
        uint256 timestamp
    );
    event ExpenseAdded(
        uint256 indexed expenseId,
        address indexed paidBy,
        uint256 amount,
        string description
    );
    event DebtSettled(
        address indexed debtor,
        address indexed creditor,
        uint256 amount,
        uint256 settlementId
    );
    event MemberRemoved(address indexed member, uint256 timestamp);
    event ExpenseUpdated(uint256 indexed expenseId, string newDescription);
    event OwnershipTransferRequested(address indexed newOwner);
    event GroupPaused(uint256 timestamp);
    event GroupUnpaused(uint256 timestamp);

    modifier onlyMember() {
        require(members[msg.sender].active, "Not a group member");
        _;
    }

    modifier validMember(address _member) {
        require(_member != address(0), "Invalid address");
        require(members[_member].active, "Member not active");
        _;
    }

    modifier notPaused() {
        require(!paused(), "Contract is paused");
        _;
    }

    modifier validExpense(uint256 _expenseId) {
        require(
            _expenseId > 0 && _expenseId < nextExpenseId,
            "Invalid expense ID"
        );
        _;
    }

    constructor(
        string memory _groupName,
        address _creator,
        string memory _creatorNickname
    ) Ownable(_creator) {
        require(bytes(_groupName).length > 0, "Group name required");
        require(
            bytes(_creatorNickname).length > 0,
            "Creator nickname required"
        );

        groupName = _groupName;
        nextExpenseId = 1;
        nextSettlementId = 1;
        factory = IExpenseFactory(msg.sender); // Factory is the deployer

        _addMember(_creator, _creatorNickname);
    }

    function addMember(
        address _member,
        string memory _nickname
    ) external onlyOwner notPaused {
        require(memberList.length < MAX_MEMBERS, "Maximum members reached");
        _addMember(_member, _nickname);

        // Notify factory
        try factory.addUserToGroup(_member, address(this)) {} catch {
            // Continue even if factory call fails
        }
    }

    function _addMember(address _member, string memory _nickname) internal {
        require(_member != address(0), "Invalid address");
        require(!members[_member].active, "Member already exists");
        require(
            bytes(_nickname).length > 0 && bytes(_nickname).length <= 32,
            "Invalid nickname length"
        );

        members[_member] = Member({
            wallet: _member,
            nickname: _nickname,
            totalOwed: 0,
            totalOwing: 0,
            active: true,
            joinedAt: block.timestamp
        });

        memberList.push(_member);
        emit MemberAdded(_member, _nickname, block.timestamp);
    }

    function addExpense(
        string memory _description,
        uint256 _amount,
        address[] memory _participants,
        bytes32 _receiptHash
    ) external onlyMember notPaused returns (uint256) {
        require(_amount > 0, "Amount must be positive");
        require(
            _participants.length > 0 &&
                _participants.length <= MAX_PARTICIPANTS_PER_EXPENSE,
            "Invalid participants count"
        );
        require(
            bytes(_description).length > 0 && bytes(_description).length <= 200,
            "Invalid description length"
        );

        // Validate all participants are active members
        for (uint i = 0; i < _participants.length; i++) {
            require(members[_participants[i]].active, "Invalid participant");
            // Check for duplicates
            for (uint j = i + 1; j < _participants.length; j++) {
                require(
                    _participants[i] != _participants[j],
                    "Duplicate participant"
                );
            }
        }

        // More precise expense splitting
        uint256 sharePerPerson = _amount / _participants.length;
        uint256 remainder = _amount % _participants.length;

        uint256[] memory shares = new uint256[](_participants.length);
        uint256 totalShares = 0;

        for (uint i = 0; i < _participants.length; i++) {
            shares[i] = sharePerPerson;
            if (i < remainder) {
                shares[i] += 1;
            }
            totalShares += shares[i];
        }

        // Ensure no precision loss
        assert(totalShares == _amount);

        uint256 expenseId = nextExpenseId++;
        expenses[expenseId] = Expense({
            id: expenseId,
            description: _description,
            totalAmount: _amount,
            paidBy: msg.sender,
            participants: _participants,
            shares: shares,
            settled: false,
            timestamp: block.timestamp,
            receiptHash: _receiptHash
        });

        expenseList.push(expenseId);

        // Update balances with overflow protection
        for (uint i = 0; i < _participants.length; i++) {
            if (_participants[i] != msg.sender) {
                balances[_participants[i]][msg.sender] += shares[i];
                members[_participants[i]].totalOwed += shares[i];
                members[msg.sender].totalOwing += shares[i];
            }
        }

        emit ExpenseAdded(expenseId, msg.sender, _amount, _description);
        return expenseId;
    }

    function settleDebt(
        address _creditor
    ) external payable nonReentrant validMember(_creditor) notPaused {
        uint256 debt = balances[msg.sender][_creditor];
        require(debt > 0, "No debt to settle");
        require(msg.value >= debt, "Insufficient payment");

        // Update balances before external calls
        balances[msg.sender][_creditor] = 0;
        members[msg.sender].totalOwed -= debt;
        members[_creditor].totalOwing -= debt;

        uint256 settlementId = nextSettlementId++;
        settlements[settlementId] = Settlement({
            debtor: msg.sender,
            creditor: _creditor,
            amount: debt,
            timestamp: block.timestamp,
            txHash: keccak256(
                abi.encodePacked(block.timestamp, msg.sender, _creditor, debt)
            )
        });

        // Transfer payment to creditor
        (bool success, ) = _creditor.call{value: debt}("");
        require(success, "Payment failed");

        // Refund excess payment
        if (msg.value > debt) {
            (bool refundSuccess, ) = msg.sender.call{value: msg.value - debt}(
                ""
            );
            require(refundSuccess, "Refund failed");
        }

        emit DebtSettled(msg.sender, _creditor, debt, settlementId);
    }

    function updateExpenseDescription(
        uint256 _expenseId,
        string memory _newDescription
    ) external validExpense(_expenseId) onlyMember notPaused {
        require(
            expenses[_expenseId].paidBy == msg.sender,
            "Only expense creator can update"
        );
        require(!expenses[_expenseId].settled, "Cannot update settled expense");
        require(
            bytes(_newDescription).length > 0 &&
                bytes(_newDescription).length <= 200,
            "Invalid description"
        );

        expenses[_expenseId].description = _newDescription;
        emit ExpenseUpdated(_expenseId, _newDescription);
    }

    function pauseGroup() external onlyOwner {
        _pause();
        emit GroupPaused(block.timestamp);
    }

    function unpauseGroup() external onlyOwner {
        _unpause();
        emit GroupUnpaused(block.timestamp);
    }

    // View functions with pagination support
    function getExpensesPaginated(
        uint256 _offset,
        uint256 _limit
    ) external view returns (Expense[] memory, uint256 total) {
        uint256 totalExpenses = expenseList.length;
        if (_offset >= totalExpenses) {
            return (new Expense[](0), totalExpenses);
        }

        uint256 end = _offset + _limit;
        if (end > totalExpenses) {
            end = totalExpenses;
        }

        uint256 length = end - _offset;
        Expense[] memory paginatedExpenses = new Expense[](length);

        for (uint256 i = 0; i < length; i++) {
            paginatedExpenses[i] = expenses[expenseList[_offset + i]];
        }

        return (paginatedExpenses, totalExpenses);
    }

    function getMembersPaginated(
        uint256 _offset,
        uint256 _limit
    ) external view returns (address[] memory, uint256 total) {
        uint256 totalMembers = memberList.length;
        if (_offset >= totalMembers) {
            return (new address[](0), totalMembers);
        }

        uint256 end = _offset + _limit;
        if (end > totalMembers) {
            end = totalMembers;
        }

        uint256 length = end - _offset;
        address[] memory paginatedMembers = new address[](length);

        for (uint256 i = 0; i < length; i++) {
            paginatedMembers[i] = memberList[_offset + i];
        }

        return (paginatedMembers, totalMembers);
    }

    function getBalance(address _member) external view returns (int256) {
        require(members[_member].active, "Member not found");
        return
            int256(members[_member].totalOwing) -
            int256(members[_member].totalOwed);
    }

    function getDebtTo(address _creditor) external view returns (uint256) {
        return balances[msg.sender][_creditor];
    }

    function getExpense(
        uint256 _expenseId
    ) external view validExpense(_expenseId) returns (Expense memory) {
        return expenses[_expenseId];
    }

    function getMemberInfo(
        address _member
    ) external view returns (Member memory) {
        require(members[_member].active, "Member not found");
        return members[_member];
    }

    function getGroupStats()
        external
        view
        returns (
            uint256 totalMembers,
            uint256 totalExpenses,
            uint256 totalAmount,
            uint256 totalSettlements
        )
    {
        totalMembers = memberList.length;
        totalExpenses = expenseList.length;
        totalSettlements = nextSettlementId - 1;

        for (uint256 i = 0; i < expenseList.length; i++) {
            totalAmount += expenses[expenseList[i]].totalAmount;
        }
    }

    function getSettlement(
        uint256 _settlementId
    ) external view returns (Settlement memory) {
        require(
            _settlementId > 0 && _settlementId < nextSettlementId,
            "Invalid settlement ID"
        );
        return settlements[_settlementId];
    }

    // Emergency functions
    function removeMember(
        address _member
    ) external onlyOwner validMember(_member) {
        require(_member != owner(), "Cannot remove owner");
        require(
            members[_member].totalOwed == 0 && members[_member].totalOwing == 0,
            "Member has outstanding balances"
        );

        members[_member].active = false;

        // Remove from member list efficiently
        for (uint i = 0; i < memberList.length; i++) {
            if (memberList[i] == _member) {
                memberList[i] = memberList[memberList.length - 1];
                memberList.pop();
                break;
            }
        }

        emit MemberRemoved(_member, block.timestamp);
    }

    function emergencyWithdraw() external onlyOwner {
        require(paused(), "Must be paused for emergency withdrawal");
        uint256 balance = address(this).balance;
        require(balance > 0, "No funds to withdraw");

        (bool success, ) = owner().call{value: balance}("");
        require(success, "Emergency withdrawal failed");
    }

    // Fallback function to receive ETH
    receive() external payable {
        // Allow contract to receive ETH for settlements and group payments
        // This function is called when ETH is sent directly to the contract
        // without any function call data

        // Only accept ETH from group members or during settlements
        require(
            members[msg.sender].active || msg.sender == owner(),
            "Only members can send ETH"
        );

        // Emit an event to track direct ETH deposits
        emit ETHReceived(msg.sender, msg.value, block.timestamp);

        // Optional: Could automatically distribute to members with negative balances
        // or hold for future settlements
    }

    // Event for tracking direct ETH deposits
    event ETHReceived(
        address indexed sender,
        uint256 amount,
        uint256 timestamp
    );
}

